sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

    // get the caller email from the email body and stored it in the "userEmail" variable.
    var userEmail = email.body.callerid;

    // Using "GlideRecord" APi and query the user table with the caller email and get the user id and map it to incident's caller id.

    var userNameGr = new GlideRecord('sys_user');
    userNameGr.addQuery('email', userEmail);
    userNameGr.query();
    if (userNameGr.next()) {
        current.caller_id = userNameGr.sys_id;

    }
    // Extract information from the email body and store it in the incident record.
    current.short_description = email.body.shortdescription;

    current.description = email.body.description;


    // Get the subcategory from the email body and stored it to "subCategory"
    var subCategory = email.body.subcategory;

    //# Determine assignment group based on the subcategory.
    if (subCategory == 'Azure') {

        current.assignment_group = gs.getProperty('x_avaso_cloud_cost.assignment.group.azure');

    } else if (subCategory == 'AWS') {

        current.assignment_group = gs.getProperty('x_avaso_cloud_cost.assignment.group.aws');

    }
    // Check if the current record can be created.
    if (current.canCreate())
        current.insert();

})(current, event, email, logger, classifier);
